mapper.select:
SELECT * FROM ( SELECT d.id AS productId, d.name, d.company, '예금' AS type, d.min_save_term AS tag1, d.max_save_term AS tag2, NULL AS tag3, CAST(d.prime_rate AS CHAR) AS title, CAST(d.rate AS CHAR) AS subTitle, d.f_sector AS fSector, d.save_term AS saveTerm, d.minimum_cost AS minAmount FROM deposit d
UNION ALL
SELECT s.id AS productId, s.name, s.company, '적금' AS type, s.min_save_term AS tag1, s.max_save_term AS tag2, NULL AS tag3, CAST(s.prime_rate AS CHAR) AS title, CAST(s.rate AS CHAR) AS subTitle, s.f_sector AS fSector, s.save_term AS saveTerm, NULL AS minAmount FROM savings s
UNION ALL
SELECT p.id AS productId, p.name, p.company, '연금' AS type, p.pension_type AS tag1, p.pension_kind AS tag2, p.risk AS tag3, CAST( IF(p.pension_type = 1, p.rate, p.last_year_profit_rate) AS CHAR ) AS title, CAST( IF(p.pension_type = 1, p.min_guarantee_rate, p.last_year_profit_rate) AS CHAR ) AS subTitle, p.f_sector AS fSector, NULL AS saveTerm, NULL AS minAmount FROM pension p
UNION ALL
SELECT f.id AS productId, f.name, f.company, '펀드' AS type, f.category AS tag1, f.theme AS tag2, f.investment_warning_grade AS tag3, CAST(f.rate AS CHAR) AS title, CAST(f.fund_price_movement AS CHAR) AS subTitle, f.f_sector AS fSector, NULL AS saveTerm, f.minimum_cost AS minAmount FROM fund f
UNION ALL
SELECT fx.id AS productId, fx.name, fx.company, '외화' AS type, fx.currency AS tag1, NULL AS tag2, NULL AS tag3, CAST(fx.rate AS CHAR) AS title, CAST(fx.rate_give_way AS CHAR) AS subTitle, fx.f_sector AS fSector, NULL AS saveTerm, NULL AS minAmount FROM forex fx ) AS unified WHERE 1=1
AND type = #{type}
AND fSector = #{fSector}
AND saveTerm = #{saveTerm}
AND CAST(minAmount AS UNSIGNED) >= #{minAmount}
